#include "fd_bench.h"

#include <chrono>
#include <string>
#include <iostream>
#include "fdeep/fdeep.hpp"

namespace
{
    void testFrugallyDeepModel(const char* modelFileName, const fdeep::tensor3& in, int its)
    {
        auto modelPath = std::string(PT_BENCHMARK_MODELS_FOLDER) + "/frugally-deep/" + modelFileName + ".json";

        try
        {
            auto model = fdeep::load_model(modelPath);

            fdeep::tensor3s inputs = { in };
            std::int64_t minElapsedMcs = std::numeric_limits<std::int64_t>::max();

            for(int i = 0; i < its; ++i)
            {
                auto startTime = std::chrono::high_resolution_clock::now();
                model.predict(inputs);
                auto elapsedTime = std::chrono::high_resolution_clock::now() - startTime;
                std::int64_t elapsedMcs = std::int64_t(std::chrono::duration_cast<std::chrono::microseconds>(elapsedTime).count());
                minElapsedMcs = std::min(minElapsedMcs, elapsedMcs);
            }

            std::cout << "frugally-deep " << modelFileName << " elapsed mcs: " << minElapsedMcs << std::endl;
        }
        catch(const std::exception& exception)
        {
            std::cerr << "frugally-deep exception: " << exception.what() << std::endl;
        }
    }
}

